import json

def validate_results(results):
    for input_name, response in results.items():
        try:
            json_response = json.loads(response)
        except json.JSONDecodeError:
            return {"utile": False, "categorie": None, "sentiment": None, "type_probleme": None, "score_gravite": None, "status": "error"}

        if not json_response["useful"]:
            return {"utile": False, "categorie": None, "sentiment": None, "type_probleme": None, "score_gravite": None, "status": "success/error"}

    # Calculate gravity score
    total_score = 0
    for input_name, response in results.items():
        try:
            json_response = json.loads(response)
        except json.JSONDecodeError:
            continue

        if 'score_gravite' in json_response:
            total_score += json_response['score_gravite']

    avg_score = total_score / len(results)
    return {"utile": True, "categorie": None, "sentiment": None, "type_probleme": None, "score_gravite": int(avg_score), "status": "success"}

