import json
import re

def validate_results(results):
    # Initialize consolidated results dictionary
    consolidated = {"utile": False, "categorie": "", "sentiment": "", "type_probleme": "", "score_gravite": 0, "status": "success/error"}

    for key, value in results.items():
        if not re.match(r'^JSON$', value):
            return None
        try:
            result = json.loads(value)
        except json.JSONDecodeError:
            return None

        if 'useful' in result and not result['useful']:
            consolidated["utile"] = True
            consolidated["score_gravite"] = 0
            return consolidated

        if 'categorie' in result:
            consolidated["categorie"] = result['categorie']

        if 'sentiment' in result:
            consolidated["sentiment"] = result['sentiment']

        if 'type_probleme' in result:
            consolidated["type_probleme"] = result['type_probleme']

        if re.search(r'\b-?\d+\b', value):
            score_gravite = int(re.findall(r'-?\d+', value)[0])
            consolidated["score_gravite"] = score_gravite

    return consolidated

