}

# Initialize output
output = {"utile": False, "categorie": "", "sentiment": "", "type_probleme": "", "score_gravite": 0, "status": "error"}

# Iterate over the input data
for key, value in data.items():
    # Parse JSON
    try:
        json_value = json.loads(value)
    except json.JSONDecodeError:
        continue

    # Update output with parsed values
    if 'utile' in json_value and json_value['utile']:
        output['utile'] = True
    if 'categorie' in json_value:
        output['categorie'] = json_value['categorie']
    if 'sentiment' in json_value:
        output['sentiment'] = json_value['sentiment']
