                results[key] = {'utile': False, 'categorie': '', 'sentiment': '', 'type_probleme': '', 'score_gravite': 0, 'status': 'error'}
        elif isinstance(value, dict):
            results[key] = {
                'utile': not value['useful'],
                'categorie': value.get('categorie', ''),
                'sentiment': value.get('sentiment', ''),
                'type_probleme': value.get('type_probleme', ''),
