import json

def validate_results(results):
    consolidated = {
        "utile": False,
        "categorie": "",
        "sentiment": "",
        "type_probleme": "",
        "score_gravite": 0,
        "status": "success/error"
    }

    for key, value in results.items():
        if value.startswith("{") and value.endswith("}"):
            try:
                data = json.loads(value)

                # Check if 'utile' is false
                if not data['useful']:
                    consolidated["score_gravite"] = 0

                # Update consolidated dictionary
                for k, v in data.items():
                    consolidated[k] = v
            except json.JSONDecodeError:
                pass

        elif key == "A5" and value.startswith("Gravity score:"):
            try:
                value = value.split(":")[1].strip()
                if value.isdigit() and int(value) > 0:
                    consolidated["score_gravite"] = int(value)

                # Update consolidated dictionary
                for k, v in {"A5": "La gravité est positive"}.items():
                    consolidated[k] = v
            except ValueError:
                pass

        elif key == "A4":
            consolidated["type_probleme"] = data['type_probleme']

        else:
            if value.startswith("{") and value.endswith("}"):
                try:
                    data = json.loads(value)

                    # Update consolidated dictionary
                    for k, v in data.items():
                        if k != 'useful':
                            consolidated[k] = v
                except json.JSONDecodeError:
                    pass

    return consolidated

