                result["type_probleme"] = value["type_probleme"]

    # Check if gravity is near 0 when utile is False
    if not result['utile']:
        result['score_gravite'] = 0

    # Determine the status based on the results
    if result['utile']:
        result['status'] = 'success'
    else:
        result['status'] = 'error'

    return result

