import json

def validate_results(results):
    consolidated_result = {"utile": False, "categorie": "", "sentiment": "", "type_probleme": "", "score_gravite": 0, "status": ""}

    for key in results:
        value = results[key]

        # Check if 'util' exists
        util_value = value.get('util', False)
        if util_value:
            consolidated_result["utile"] = True

        # Check 'categorie'
        categorie_value = value.get("categorie", "")
        consolidated_result["categorie"] = categorie_value.strip()

        # Check 'sentiment'
        sentiment_value = value.get("sentiment")
        if sentiment_value:
            consolidated_result["sentiment"] = sentiment_value
        else:
            sentiment_values = value.get('sentiment', '')
            if sentiment_values == "":
                sentiment_values = value.get('colère')
                if sentiment_values:
                    consolidated_result["sentiment"] = "colère"

        # Check 'type_probleme'
        type_probleme_value = value.get("type_probleme", "")
        consolidated_result["type_probleme"] = type_probleme_value.strip()

        # Check 'score_gravite'
        score_gravite_value = value.get('score_gravite')
        if score_gravite_value:
            consolidated_result['score_gravite'] = int(score_gravite_value)

    return consolidated_result

