    for key, value in inputs.items():
        if value.startswith('\"'):
            try:
                json.loads(value)
            except json.JSONDecodeError:
                continue

            result = {}

            if 'categorie' in value:
                result['categorie'] = value.split("'")[1]

            if 'sentiment' in value:
                sentiment = value.split("'")[2]
                result['sentiment'] = sentiment

                # Analyze the sentiment
                if sentiment == 'frustration':
                    results[key] = {'utile': True, 'categorie': result.get('categorie', ''), 'sentiment': result.get('sentiment', ''), 'type_probleme': 'probleme', 'score_gravite': 0, 'status': 'success'}
                else:
                    # Calculate the score_gravite
                    # For simplicity, we'll just use a simple score system: 
                    # - 0 for neutral sentiment
                    # - 1 for negative sentiment
                    # - 2 for positive sentiment
                    if sentiment == 'frustration':
                        result['score_gravite'] = 1
                    elif sentiment in ['colere', 'saturation']: # Adding saturation to the list of possible scores
                        result['score_gravite'] = 0
                    else:
                        result['score_gravite'] = 2

            if 'type_probleme' in value:
                result['type_probleme'] = value.split("'")[1]

            # Check if gravity is near 0 if utile is False
            if 'utile' not in results and ('categorie' in result and 'sentiment' in result):
                if not result['utile']:
                    result['score_gravite'] = 0

            if 'score_gravite' not in result:
                # Check if gravity should be near 0
                if key not in ['A1', 'A2'] and not (result.get('categorie') == 'probleme' and result.get('sentiment') == 'frustration'):
                    result['score_gravite'] = 0

            results[key] = result

    return results

